<template>
  <div>
  {{num}}
</div>
</template>

<script>
export default {
  props: {
    startVal: {
      type: Number,
      required: false,
      default: 0
    },
    endVal: {
      type: Number,
      required: false,
      default: 2017
    },
    duration: {
      type: Number,
      required: false,
      default: 1000
    }
  },
  data () {
    return {
      num: 0,
      interval: ''
    }
  },
  mounted(){
    this.interval = setInterval(this.initCountUp, this.duration)
  },
  methods:{
    initCountUp(){
      if (this.num <= this.endVal) {
        this.startVal += 1
        this.num = this.startVal
      } else {
         clearInterval(this.interval)
      }
    }
  }
}
</script>